mapper.update:
update category_brand
 set update_time=now()
,category_id=#{categoryId} , brand_id=#{brandId} where id=#{id}

mapper.select:
select category_brand.id as id , brand_id as brandId ,category_id as categoryId ,brand.name as brandName ,logo, category.name as categoryName, brand.create_time as createTime from category_brand left join brand on brand.id=category_brand.brand_id left join category on category.id=category_brand.category_id
brand.id=#{brandId}
and category.id=#{categoryId}
and brand.is_deleted=0
